document.addEventListener("DOMContentLoaded",function(){const dialogue=document.querySelector(".gibby-dialogue");function toggleDialogue(){dialogue.style.display="block";setTimeout(()=>{dialogue.style.display="none";setTimeout(toggleDialogue,2e3)},2e4)}setTimeout(toggleDialogue,1e3)});document.addEventListener("DOMContentLoaded",function(){let slideIndex=0;showSlides();function showSlides(){const slides=document.getElementsByClassName("slides");for(let i=0;i<slides.length;i++){slides[i].style.display="none"}slideIndex++;if(slideIndex>slides.length){slideIndex=1}slides[slideIndex-1].style.display="block";setTimeout(showSlides,6e3)}});document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".platform-download").forEach(function(block){const mainBtn=block.querySelector(".big-download-btn, .btnappstore, .btngogoleplay");block.querySelectorAll(".format-option").forEach(function(opt){opt.addEventListener("click",function(){block.querySelectorAll(".format-option").forEach(o=>o.classList.remove("selected"));opt.classList.add("selected");mainBtn.href=opt.dataset.href;mainBtn.textContent="Download "+opt.dataset.label;if(block.dataset.platform==="macos"){if(opt.dataset.label==="App Store"){mainBtn.classList.remove("big-download-btn","macos");mainBtn.classList.add("btnappstore");mainBtn.textContent=""}else{mainBtn.classList.remove("btnappstore");mainBtn.classList.add("big-download-btn","macos")}}if(block.dataset.platform==="android"){if(opt.dataset.label==="Play Store"){mainBtn.classList.remove("big-download-btn","android");mainBtn.classList.add("btngogoleplay");mainBtn.textContent=""}else{mainBtn.classList.remove("btngogoleplay");mainBtn.classList.add("big-download-btn","android")}}if(block.dataset.platform==="linux"){if(opt.dataset.label==="FlatHub"){mainBtn.classList.remove("big-download-btn","linux");mainBtn.classList.add("btnflathub");mainBtn.textContent=""}else{mainBtn.classList.remove("btnflathub");mainBtn.classList.add("big-download-btn","linux")}}})})})});